import React from 'react'
import { Modal, Header, Form, Image, Button, Container } from 'semantic-ui-react'

class LoginForm extends React.Component {
    state = {
        modalActive: false,
		username: '',
		password: '',
		name: '',
		birthdate: '',
		bio: '',
		img: '',
		region: '',
    }

    render() {
        return (
            // <Modal
            //     trigger={<Button circular={true}></Button>} 
            //     open={ undefined }
            //     closeOnEscape={true}
            //     closeOnDimmerClick={true}
            //     onClose={ undefined}
            // >
            <React.Fragment>
                <Container>
                    <Modal.Header>
                        <p><em><small>Italics small Header</small></em></p>
                    </Modal.Header>
                        <Modal.Content image />
                            <Image wrapped size='medium' src="" />
                            <Modal.Description style={{width: "100%"}} />
                            <Header>Profile Details</Header>

                            <Form>
                                <Form.Group widths='equal'>
                                <Form.Input 
                                    fluid 
                                    name="username" 
                                    label="Username"
                                    placeholder="user123"
                                />
                                <Form.Input 
                                    fluid 
                                    type="password"
                                    name="password" 
                                    label="Password"
                                    placeholder="password..."
                                />
                                </Form.Group>

                                <React.Fragment> 
                                    <Button type="submit" floated="right"  primary >Sign Up</Button>
                                </React.Fragment>

                                <Button.Group floated="right">
                                    <Button type="submit"  primary >
                                        Sign In
                                    </Button>
                                </Button.Group>
                            </Form>
                </Container>
            </React.Fragment>
        )
    }
}

export default LoginForm
