import React from 'react'
import { Card, Image } from 'semantic-ui-react'

class MovieCard extends React.Component {
    render() {
        let { poster_path, original_title, release_date, overview, vote_average } = this.props.movie
        return (
            <React.Fragment>
                {console.log(this.props)}
                <Card>
                    <Image src={poster_path} wrapped ui={false} />
                    <Card.Content>
                        <Card.Header>{original_title}</Card.Header>
                        <Card.Meta>
                            <span className='date'>Release Date: {release_date}</span>
                        </Card.Meta>
                        <Card.Description>
                            {overview}
                        </Card.Description>
                    </Card.Content>
                    <Card.Content extra>
                        Rating: {vote_average}
                    </Card.Content>
                </Card>

            </React.Fragment>
        )
    }
}

export default MovieCard
